package io.renren.client.controller;import io.renren.client.dto.VerifyDTO;import io.renren.utils.R;import org.slf4j.LoggerFactory;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.net.InetAddress;import java.net.NetworkInterface;import java.net.SocketException;import java.net.UnknownHostException;/** * @Classname ConfigController * @Pakage: io.renren.client.controller * @Author: hzr * @Description TODO * @Date 2024/3/22 14:26 * @Created by 22906 * @Version 1.0.0 */@RestController@RequestMapping("/config")public class ConfigController {    @PostMapping("/verify")    public R verify(@RequestBody VerifyDTO dto) {        LoggerFactory.getLogger(ConfigController.class).info("verify: {}", dto);        try {            InetAddress ip = InetAddress.getLocalHost();            NetworkInterface network = NetworkInterface.getByInetAddress(ip);            byte[] mac = network.getHardwareAddress();            StringBuilder sb = new StringBuilder();            for (int i = 0; i < mac.length; i++) {                sb.append(String.format("%02X%s", mac[i], (i < mac.length - 1) ? "-" : ""));            }            System.out.println("MAC address : " + sb.toString());        } catch (UnknownHostException e) {            e.printStackTrace();        } catch (SocketException e){            e.printStackTrace();        }        return R.ok().put("status", 2);    }}