package io.renren.service;import com.github.pagehelper.Page;import com.github.pagehelper.PageHelper;import io.renren.dao.GeneratorDao;import io.renren.dao.YcanOfdFilesDao;import io.renren.entity.YcanOfdFilesEntity;import io.renren.utils.GenUtils;import io.renren.utils.PageUtils;import io.renren.utils.Query;import org.apache.tomcat.util.http.fileupload.IOUtils;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.io.ByteArrayOutputStream;import java.util.List;import java.util.Map;import java.util.zip.ZipOutputStream;/** * @Classname YcanOfdFilesService * @Pakage: io.renren.service * @Author: hzr * @Description TODO * @Date 2024/3/7 15:01 * @Created by 22906 * @Version 1.0.0 */@Servicepublic class YcanOfdFilesService {    @Resource    private YcanOfdFilesDao ycanOfdFilesDao;    public PageUtils queryList(Query query) {        Page<?> page = PageHelper.startPage(query.getPage(), query.getLimit());        List<Map<String, Object>> list = ycanOfdFilesDao.queryList(query);        return new PageUtils(list, (int) page.getTotal(), query.getLimit(), query.getPage());    }    public int save(YcanOfdFilesEntity ycanOfdFilesEntity) {        return ycanOfdFilesDao.save(ycanOfdFilesEntity);    }    public YcanOfdFilesEntity findById(int i) {        return ycanOfdFilesDao.findById(i);    }    public int deleteById(int id) {        return ycanOfdFilesDao.deleteById(id);    }}